windowsSetupScript.value:
[concat('# Copilot Agent 365 - Windows PowerShell Setup Script with Python 3.11
# This script ensures Python 3.11 is installed (required for Azure Functions v4)
# Save as setup.ps1 and run with: .\setup.ps1

Write-Host "========================================" -ForegroundColor Cyan
Write-Host "   Copilot Agent 365 - Setup (Python 3.11)" -ForegroundColor Cyan
Write-Host "========================================" -ForegroundColor Cyan
Write-Host ""
Write-Host "This script will install Python 3.11 (required for Azure Functions)" -ForegroundColor Yellow
Write-Host ""

# Check if running as Administrator
$isAdmin = ([Security.Principal.WindowsPrincipal] [Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole([Security.Principal.WindowsBuiltInRole] "Administrator")
if (-not $isAdmin) {
    Write-Host "[INFO] Not running as Administrator. Some installations may require admin rights." -ForegroundColor Yellow
    Write-Host "If any installations fail, please run PowerShell as Administrator." -ForegroundColor Yellow
    Write-Host ""
}

# Function to check if a command exists
function Test-Command {
    param($Command)
    try {
        Get-Command $Command -ErrorAction Stop | Out-Null
        return $true
    } catch {
        return $false
    }
}

# Check execution policy
$executionPolicy = Get-ExecutionPolicy
if ($executionPolicy -eq "Restricted") {
    Write-Host "Setting PowerShell execution policy..." -ForegroundColor Yellow
    Set-ExecutionPolicy -ExecutionPolicy RemoteSigned -Scope CurrentUser -Force
}

# INSTALL PYTHON 3.11 SPECIFICALLY
Write-Host "Checking for Python 3.11..." -ForegroundColor Yellow
$python311Found = $false
$pythonCmd = $null

# Check if Python 3.11 is already installed
$pythonPaths = @(
    "$env:LOCALAPPDATA\Programs\Python\Python311\python.exe",
    "C:\Python311\python.exe",
    "C:\Program Files\Python311\python.exe",
    "C:\Program Files (x86)\Python311\python.exe"
)

foreach ($path in $pythonPaths) {
    if (Test-Path $path) {
        try {
            $version = & $path --version 2>&1
            if ($version -match "Python 3\.11") {
                Write-Host "[OK] Found Python 3.11 at: $path" -ForegroundColor Green
                $pythonCmd = $path
                $python311Found = $true
                break
            }
        } catch {}
    }
}

# Check system Python commands for 3.11
if (-not $python311Found) {
    $commands = @("python3.11", "python311", "python3", "python", "py")
    foreach ($cmd in $commands) {
        if (Test-Command $cmd) {
            try {
                if ($cmd -eq "py") {
                    # Try to use py launcher with Python 3.11
                    $version = py -3.11 --version 2>&1
                    if ($version -match "Python 3\.11") {
                        Write-Host "[OK] Found Python 3.11 via py launcher" -ForegroundColor Green
                        $pythonCmd = "py -3.11"
                        $python311Found = $true
                        break
                    }
                } else {
                    $version = & $cmd --version 2>&1
                    if ($version -match "Python 3\.11") {
                        Write-Host "[OK] Found Python 3.11: $version" -ForegroundColor Green
                        $pythonCmd = $cmd
                        $python311Found = $true
                        break
                    }
                }
            } catch {}
        }
    }
}

# Install Python 3.11 if not found
if (-not $python311Found) {
    Write-Host "[WARNING] Python 3.11 not found. Installing Python 3.11..." -ForegroundColor Yellow
    Write-Host "This will take a few minutes..." -ForegroundColor Gray

    $pythonInstaller = "$env:TEMP\python-3.11-installer.exe"
    # Use Python 3.11.9 which is the latest 3.11 release
    $pythonUrl = "https://www.python.org/ftp/python/3.11.9/python-3.11.9-amd64.exe"

    Write-Host "Downloading Python 3.11 installer..." -ForegroundColor Gray
    Invoke-WebRequest -Uri $pythonUrl -OutFile $pythonInstaller -UseBasicParsing

    Write-Host "Installing Python 3.11 (this may take 2-3 minutes)..." -ForegroundColor Yellow
    Start-Process -FilePath $pythonInstaller -ArgumentList "/quiet", "InstallAllUsers=1", "PrependPath=1", "Include_test=0" -Wait

    # Refresh PATH
    $env:Path = [System.Environment]::GetEnvironmentVariable("Path","Machine") + ";" + [System.Environment]::GetEnvironmentVariable("Path","User")

    # Check installation paths again
    foreach ($path in $pythonPaths) {
        if (Test-Path $path) {
            try {
                $version = & $path --version 2>&1
                if ($version -match "Python 3\.11") {
                    Write-Host "[OK] Python 3.11 installed successfully at: $path" -ForegroundColor Green
                    $pythonCmd = $path
                    $python311Found = $true
                    break
                }
            } catch {}
        }
    }

    if (-not $python311Found) {
        Write-Host "[ERROR] Python 3.11 installation failed." -ForegroundColor Red
        Write-Host "Please install Python 3.11 manually from: https://www.python.org/downloads/release/python-3119/" -ForegroundColor Yellow
        Read-Host "Press Enter to exit"
        exit 1
    }

    Remove-Item $pythonInstaller -Force -ErrorAction SilentlyContinue
}

Write-Host "Using Python command: $pythonCmd" -ForegroundColor Green

# INSTALL GIT IF NOT FOUND
Write-Host "Checking for Git..." -ForegroundColor Yellow
if (Test-Command "git") {
    $gitVersion = git --version
    Write-Host "[OK] Found $gitVersion" -ForegroundColor Green
} else {
    Write-Host "[WARNING] Git not found. Installing Git..." -ForegroundColor Yellow

    if (Test-Command "winget") {
        Write-Host "Installing Git via winget..." -ForegroundColor Gray
        winget install --id Git.Git -e --silent --accept-package-agreements --accept-source-agreements
    } else {
        $gitInstaller = "$env:TEMP\git-installer.exe"
        $gitUrl = "https://github.com/git-for-windows/git/releases/download/v2.43.0.windows.1/Git-2.43.0-64-bit.exe"

        Write-Host "Downloading Git installer..." -ForegroundColor Gray
        Invoke-WebRequest -Uri $gitUrl -OutFile $gitInstaller -UseBasicParsing

        Write-Host "Installing Git (this may take a minute)..." -ForegroundColor Yellow
        Start-Process -FilePath $gitInstaller -ArgumentList "/VERYSILENT", "/NORESTART" -Wait

        Remove-Item $gitInstaller -Force -ErrorAction SilentlyContinue
    }

    $env:Path = [System.Environment]::GetEnvironmentVariable("Path","Machine") + ";" + [System.Environment]::GetEnvironmentVariable("Path","User")

    if (Test-Command "git") {
        Write-Host "[OK] Git installed successfully" -ForegroundColor Green
    } else {
        Write-Host "[ERROR] Git installation failed" -ForegroundColor Red
        Read-Host "Press Enter to exit"
        exit 1
    }
}

# INSTALL NODE.JS IF NOT FOUND
Write-Host "Checking for Node.js..." -ForegroundColor Yellow
if (Test-Command "node") {
    $nodeVersion = node --version
    Write-Host "[OK] Found Node.js $nodeVersion" -ForegroundColor Green
} else {
    Write-Host "[WARNING] Node.js not found. Installing Node.js..." -ForegroundColor Yellow

    if (Test-Command "winget") {
        Write-Host "Installing Node.js via winget..." -ForegroundColor Gray
        winget install --id OpenJS.NodeJS.LTS -e --silent --accept-package-agreements --accept-source-agreements
    } else {
        $nodeInstaller = "$env:TEMP\node-installer.msi"
        $nodeUrl = "https://nodejs.org/dist/v20.11.0/node-v20.11.0-x64.msi"

        Write-Host "Downloading Node.js installer..." -ForegroundColor Gray
        Invoke-WebRequest -Uri $nodeUrl -OutFile $nodeInstaller -UseBasicParsing

        Write-Host "Installing Node.js (this may take a minute)..." -ForegroundColor Yellow
        Start-Process msiexec.exe -ArgumentList "/i", $nodeInstaller, "/quiet", "/norestart" -Wait

        Remove-Item $nodeInstaller -Force -ErrorAction SilentlyContinue
    }

    $env:Path = [System.Environment]::GetEnvironmentVariable("Path","Machine") + ";" + [System.Environment]::GetEnvironmentVariable("Path","User")

    if (Test-Command "node") {
        $nodeVersion = node --version
        Write-Host "[OK] Node.js installed successfully: $nodeVersion" -ForegroundColor Green
    } else {
        Write-Host "[ERROR] Node.js installation failed" -ForegroundColor Red
        Read-Host "Press Enter to exit"
        exit 1
    }
}

# Check for npm
Write-Host "Checking for npm..." -ForegroundColor Yellow
if (Test-Command "npm") {
    $npmVersion = npm --version
    Write-Host "[OK] Found npm $npmVersion" -ForegroundColor Green
} else {
    Write-Host "[ERROR] npm not found!" -ForegroundColor Red
    Read-Host "Press Enter to exit"
    exit 1
}

# INSTALL AZURE FUNCTIONS CORE TOOLS
Write-Host "Checking for Azure Functions Core Tools..." -ForegroundColor Yellow
if (Test-Command "func") {
    $funcVersion = func --version
    Write-Host "[OK] Found Azure Functions Core Tools $funcVersion" -ForegroundColor Green
} else {
    Write-Host "[WARNING] Azure Functions Core Tools not found" -ForegroundColor Yellow
    Write-Host "Installing Azure Functions Core Tools..." -ForegroundColor Yellow
    npm install -g azure-functions-core-tools@4

    $env:Path = [System.Environment]::GetEnvironmentVariable("Path","Machine") + ";" + [System.Environment]::GetEnvironmentVariable("Path","User")

    if (Test-Command "func") {
        Write-Host "[OK] Azure Functions Core Tools installed successfully" -ForegroundColor Green
    } else {
        Write-Host "[WARNING] Could not install Azure Functions Core Tools" -ForegroundColor Yellow
    }
}

# INSTALL AZURE CLI (optional)
Write-Host "Checking for Azure CLI..." -ForegroundColor Yellow
if (Test-Command "az") {
    Write-Host "[OK] Found Azure CLI" -ForegroundColor Green
} else {
    Write-Host "[INFO] Azure CLI not found (optional for deployment)" -ForegroundColor Yellow
}

Write-Host ""
Write-Host "======================================" -ForegroundColor Green
Write-Host "All prerequisites installed/verified!" -ForegroundColor Green
Write-Host "======================================" -ForegroundColor Green
Write-Host ""

# Clone or update repository
Write-Host "Setting up project repository..." -ForegroundColor Yellow
if (Test-Path "Copilot-Agent-365") {
    Write-Host "Updating existing repository..." -ForegroundColor Gray
    Set-Location "Copilot-Agent-365"
    git pull
} else {
    Write-Host "Cloning repository..." -ForegroundColor Gray
    git clone https://github.com/kody-w/Copilot-Agent-365.git
    if (Test-Path "Copilot-Agent-365") {
        Write-Host "[OK] Repository cloned successfully" -ForegroundColor Green
        Set-Location "Copilot-Agent-365"
    } else {
        Write-Host "[ERROR] Failed to clone repository" -ForegroundColor Red
        Read-Host "Press Enter to exit"
        exit 1
    }
}

# Create local.settings.json with actual Azure values from deployment
Write-Host "Creating configuration file..." -ForegroundColor Yellow
$settings = @{
    IsEncrypted = $false
    Values = @{
        AzureWebJobsStorage = "', concat('DefaultEndpointsProtocol=https;AccountName=', parameters('storageAccountName'), ';EndpointSuffix=', environment().suffixes.storage, ';AccountKey=',listKeys(variables('storageAccountId'), '2022-09-01').keys[0].value), '"
        FUNCTIONS_WORKER_RUNTIME = "python"
        AZURE_OPENAI_API_KEY = "', listKeys(variables('openAIResourceId'), '2023-05-01').key1, '"
        AZURE_OPENAI_ENDPOINT = "', reference(variables('openAIResourceId')).endpoint, '"
        AZURE_OPENAI_API_VERSION = "2024-02-01"
        AZURE_OPENAI_DEPLOYMENT_NAME = "gpt-deployment"
        AZURE_FILES_SHARE_NAME = "', variables('fileShareName'), '"
        ASSISTANT_NAME = "Copilot Agent 365"
        CHARACTERISTIC_DESCRIPTION = "Enterprise AI assistant integrated with Microsoft 365"
    }
}
$settings | ConvertTo-Json -Depth 3 | Set-Content "local.settings.json"
Write-Host "[OK] Configuration file created with your Azure deployment values" -ForegroundColor Green

# Deploy to Azure (optional)
if (Test-Command "az") {
    Write-Host ""
    Write-Host "Preparing Azure deployment..." -ForegroundColor Yellow
    Write-Host "Deploying to Azure..." -ForegroundColor Yellow

    if (Test-Path "deploy.zip") { Remove-Item "deploy.zip" }

    Write-Host "Creating deployment package..." -ForegroundColor Gray
    Compress-Archive -Path * -DestinationPath deploy.zip -Force -CompressionLevel Optimal

    Write-Host "Uploading to Azure (this may take a minute)..." -ForegroundColor Gray
    az functionapp deployment source config-zip --resource-group "', resourceGroup().name, '" --name "', parameters('functionAppName'), '" --src deploy.zip

    if ($LASTEXITCODE -eq 0) {
        Write-Host "[OK] Successfully deployed to Azure!" -ForegroundColor Green
    } else {
        Write-Host "[WARNING] Azure deployment failed - run ''az login'' first" -ForegroundColor Yellow
    }

    if (Test-Path "deploy.zip") { Remove-Item "deploy.zip" }
} else {
    Write-Host "[INFO] Azure CLI not installed - skipping deployment" -ForegroundColor Yellow
}

# Setup Python environment
Write-Host ""
Write-Host "Setting up Python 3.11 virtual environment..." -ForegroundColor Yellow

if (Test-Path ".venv") {
    Write-Host "Removing existing virtual environment..." -ForegroundColor Gray
    Remove-Item -Recurse -Force ".venv"
}

Write-Host "Creating virtual environment with Python 3.11..." -ForegroundColor Gray

# FIXED: Handle Python paths with spaces properly
if ($pythonCmd -match " -") {
    # Handle commands like "py -3.11"
    $parts = $pythonCmd -split " ", 2
    & $parts[0] $parts[1] -m venv .venv
} elseif ($pythonCmd -match " ") {
    # Handle paths with spaces like "C:\Program Files\Python311\python.exe"
    # Use Invoke-Expression for paths with spaces
    $venvCommand = "& `"$pythonCmd`" -m venv .venv"
    Invoke-Expression $venvCommand
} else {
    # Simple command without spaces
    & $pythonCmd -m venv .venv
}

if (Test-Path ".venv") {
    Write-Host "[OK] Virtual environment created" -ForegroundColor Green
} else {
    Write-Host "[ERROR] Failed to create virtual environment" -ForegroundColor Red
    Write-Host "Trying alternative method..." -ForegroundColor Yellow

    # Alternative: Try using the short path name
    $shortPath = (New-Object -ComObject Scripting.FileSystemObject).GetFile($pythonCmd).ShortPath
    if ($shortPath) {
        Write-Host "Using short path: $shortPath" -ForegroundColor Gray
        & $shortPath -m venv .venv
    }

    if (Test-Path ".venv") {
        Write-Host "[OK] Virtual environment created with alternative method" -ForegroundColor Green
    } else {
        Write-Host "[ERROR] Could not create virtual environment" -ForegroundColor Red
        Write-Host "Please try running this command manually:" -ForegroundColor Yellow
        Write-Host "  `"$pythonCmd`" -m venv .venv" -ForegroundColor White
        Read-Host "Press Enter to exit"
        exit 1
    }
}

Write-Host "Installing Python packages..." -ForegroundColor Yellow
& ".\.venv\Scripts\python.exe" -m pip install --upgrade pip --quiet

if (Test-Path "requirements.txt") {
    Write-Host "Installing from requirements.txt..." -ForegroundColor Gray
    & ".\.venv\Scripts\pip.exe" install -r requirements.txt
} else {
    Write-Host "Installing default packages..." -ForegroundColor Gray
    & ".\.venv\Scripts\pip.exe" install openai==1.55.3
    & ".\.venv\Scripts\pip.exe" install httpx==0.27.2
    & ".\.venv\Scripts\pip.exe" install azure-functions==1.18.0
    & ".\.venv\Scripts\pip.exe" install azure-storage-file==2.1.0
    & ".\.venv\Scripts\pip.exe" install "pydantic==1.10.13"
}
Write-Host "[OK] Python packages installed" -ForegroundColor Green

# Create run scripts
Write-Host ""
Write-Host "Creating run scripts..." -ForegroundColor Yellow

$runPs1Content = @''
# Copilot Agent 365 - Run Script
Write-Host "Starting Copilot Agent 365..." -ForegroundColor Cyan
Write-Host ""

# Activate virtual environment
& ".\.venv\Scripts\Activate.ps1"

Write-Host "Local URL: http://localhost:7071/api/businessinsightbot_function" -ForegroundColor Green
Write-Host "Press Ctrl+C to stop" -ForegroundColor Yellow
Write-Host ""

# Start the function with Python 3.11
func start
''@
Set-Content -Path "run.ps1" -Value $runPs1Content

$runBatContent = @''
@echo off
echo Starting Copilot Agent 365...
call .venv\Scripts\activate.bat
echo.
echo Local URL: http://localhost:7071/api/businessinsightbot_function
echo Press Ctrl+C to stop
echo.
func start
''@
Set-Content -Path "run.bat" -Value $runBatContent

Write-Host "[OK] Run scripts created (run.ps1 and run.bat)" -ForegroundColor Green

# Complete!
Write-Host ""
Write-Host "================================================" -ForegroundColor Green
Write-Host "     SETUP COMPLETE! Everything is ready!" -ForegroundColor Green
Write-Host "     Using Python 3.11 (Azure Functions compatible)" -ForegroundColor Green
Write-Host "================================================" -ForegroundColor Green
Write-Host ""

Write-Host "Azure Function URL:" -ForegroundColor Cyan
Write-Host "  https://', reference(variables('functionAppId')).defaultHostName, '/api/businessinsightbot_function?code=', listkeys(concat(resourceId('Microsoft.Web/sites', parameters('functionAppName')), '/host/default'), '2022-03-01').functionKeys.default, '" -ForegroundColor White
Write-Host ""

Write-Host "To run locally:" -ForegroundColor Cyan
Write-Host "  .\run.ps1  (PowerShell)" -ForegroundColor White
Write-Host "  run.bat    (Command Prompt)" -ForegroundColor White
Write-Host ""

Write-Host "Test with PowerShell:" -ForegroundColor Cyan
$testCommand = ''Invoke-RestMethod -Uri "http://localhost:7071/api/businessinsightbot_function" -Method Post -Body "{`"user_input`":`"Hello`",`"conversation_history`":[]}" -ContentType "application/json"''
Write-Host "  $testCommand" -ForegroundColor White
Write-Host ""

$response = Read-Host "Would you like to start the local server now? (y/n)"
if ($response -eq ''y'' -or $response -eq ''Y'') {
    & .\run.ps1
} else {
    Write-Host ""
    Write-Host "Setup complete! Run ''.\run.ps1'' whenever you want to start the bot." -ForegroundColor Green
}
')]
